{
  "name": "gitvan",
  "version": "2.0.0",
  "type": "module",
  "description": "GitVan v2 - AI-powered Git workflow automation with composables and Nunjucks templates",
  "repository": "sac/gitvan",
  "license": "MIT",
  "main": "./src/index.mjs",
  "types": "./types/index.d.ts",
  "bin": {
    "gitvan": "./bin/gitvan.mjs"
  },
  "exports": {
    ".": {
      "import": "./src/index.mjs",
      "types": "./types/index.d.ts"
    },
    "./define": {
      "import": "./define.mjs",
      "types": "./types/index.d.ts"
    },
    "./useGit": {
      "import": "./useGit.mjs",
      "types": "./types/index.d.ts"
    },
    "./useTemplate": {
      "import": "./useTemplate.mjs",
      "types": "./types/index.d.ts"
    },
    "./daemon": {
      "import": "./daemon.mjs",
      "types": "./types/index.d.ts"
    },
    "./composables": {
      "import": "./src/composables/index.mjs",
      "types": "./types/index.d.ts"
    },
    "./runtime": {
      "import": "./src/runtime/boot.mjs",
      "types": "./types/index.d.ts"
    }
  },
  "scripts": {
    "dev": "node src/cli.mjs",
    "build": "echo 'Pure JS - no build needed'",
    "test": "vitest",
    "lint": "eslint src/",
    "lint:fix": "eslint src/ --fix",
    "typecheck": "tsc --noEmit",
    "cli": "node bin/gitvan.mjs",
    "gitvan": "node bin/gitvan.mjs",
    "clean": "rm -rf node_modules dist",
    "release": "npm test && npm publish"
  },
  "dependencies": {
    "c12": "^2.0.4",
    "citty": "^0.1.6",
    "currency.js": "^2.0.4",
    "defu": "^6.1.4",
    "hookable": "^5.5.3",
    "inflection": "^3.0.2",
    "klona": "^2.0.6",
    "nunjucks": "^3.2.4",
    "pathe": "^1.1.2",
    "tinyglobby": "^0.2.15",
    "unctx": "^2.4.1",
    "unrouting": "^0.0.1"
  },
  "devDependencies": {
    "@types/node": "^20.14.0",
    "@types/nunjucks": "^3.2.6",
    "@vitest/coverage-v8": "^2.0.5",
    "eslint": "^9.33.0",
    "eslint-config-unjs": "^0.5.0",
    "prettier": "^3.6.2",
    "tempy": "^3.1.0",
    "typescript": "^5.5.0",
    "vitest": "^2.1.9"
  },
  "engines": {
    "node": ">=18.0.0",
    "pnpm": ">=8.0.0"
  },
  "packageManager": "pnpm@9.7.1",
  "keywords": [
    "git",
    "ai",
    "automation",
    "llm",
    "workflow",
    "repository",
    "intelligence",
    "devtools"
  ]
}
