// src/composables/template.mjs
// GitVan v2 — useTemplate() with inflection filters and config discovery

import { promises as fs } from "node:fs";
import { join, dirname, resolve as presolve, isAbsolute } from "pathe";
import nunjucks from "nunjucks";
import * as inflection from "inflection";
import { useGitVan, tryUseGitVan } from "../core/context.mjs";
import { findTemplatesDir, getConfigValue } from "../utils/config-finder.mjs";

const _envCache = new Map(); // key -> nunjucks.Environment

/**
 * Bind context and resolve template configuration
 * Integrates with config discovery for robust template path resolution
 */
async function bindContext(opts = {}) {
  let ctx;
  try {
    ctx = useGitVan();
  } catch {
    ctx = tryUseGitVan?.() || null;
  }

  const root = (ctx && ctx.cwd) || process.cwd();

  // Try to discover templates directory from config if not explicitly provided
  let templatePaths = [];
  if (Array.isArray(opts.paths) && opts.paths.length) {
    templatePaths = opts.paths;
  } else {
    // Use config discovery to find templates directory
    const templatesResult = await findTemplatesDir(root);
    if (templatesResult) {
      templatePaths = [templatesResult.templatesDir];

      // Merge discovered config with context config
      if (templatesResult.config && !ctx?.config) {
        ctx = ctx || {};
        ctx.config = templatesResult.config;
      }
    } else {
      // Fallback to context config or default
      const cfgDirs =
        (ctx &&
          ctx.config &&
          ctx.config.templates &&
          ctx.config.templates.dirs) ||
        [];
      templatePaths = cfgDirs.length ? cfgDirs : ["templates"];
    }
  }

  // Resolve all paths relative to root
  const paths = templatePaths.map((p) => presolve(root, p));

  // Configuration options with precedence: opts > ctx.config > defaults
  const autoescape =
    typeof opts.autoescape === "boolean"
      ? opts.autoescape
      : getConfigValue(ctx?.config, "templates.autoescape", false);

  const noCache =
    typeof opts.noCache === "boolean"
      ? opts.noCache
      : getConfigValue(ctx?.config, "templates.noCache", true);

  const nowISO =
    typeof ctx?.now === "function" ? ctx.now() : process.env.GITVAN_NOW || null;

  return { root, paths, autoescape, noCache, nowISO, ctx };
}

/**
 * Generate cache key for environment instances
 */
function envKey({ paths, autoescape, noCache }) {
  return JSON.stringify({ paths, autoescape, noCache });
}

/**
 * Create Nunjucks environment with all filters and guards
 */
function createEnv({ paths, autoescape, noCache }) {
  const loader = new nunjucks.FileSystemLoader(paths, { noCache });
  const env = new nunjucks.Environment(loader, {
    autoescape,
    throwOnUndefined: true,
  });

  // Determinism guards - prevent non-deterministic operations
  env.addGlobal("now", () => {
    throw new Error("Templates must not call now(); inject a value.");
  });
  env.addGlobal("random", () => {
    throw new Error("Templates must not use random(); inject values.");
  });

  // Built-in pure filters
  env.addFilter("json", (v, space = 0) => JSON.stringify(v, null, space));
  env.addFilter("slug", (s) =>
    String(s)
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, "-")
      .replace(/(^-|-$)/g, ""),
  );
  env.addFilter("upper", (s) => String(s).toUpperCase());
  env.addFilter("lower", (s) => String(s).toLowerCase());
  env.addFilter("pad", (s, n = 2, ch = "0") => String(s).padStart(n, ch));

  // Inflection filters (direct passthrough to inflection library)
  env.addFilter("pluralize", (s, plural) =>
    inflection.pluralize(String(s), plural),
  );
  env.addFilter("singularize", (s, singular) =>
    inflection.singularize(String(s), singular),
  );
  env.addFilter("inflect", (s, count, singular, plural) =>
    inflection.inflect(String(s), Number(count), singular, plural),
  );
  env.addFilter("camelize", (s, lowFirst = false) =>
    inflection.camelize(String(s), !!lowFirst),
  );
  env.addFilter("underscore", (s, allUpper = false) =>
    inflection.underscore(String(s), !!allUpper),
  );
  env.addFilter("humanize", (s, lowFirst = false) =>
    inflection.humanize(String(s), !!lowFirst),
  );
  env.addFilter("capitalize", (s) => inflection.capitalize(String(s)));
  env.addFilter("dasherize", (s) => inflection.dasherize(String(s)));
  env.addFilter("titleize", (s) => inflection.titleize(String(s)));
  env.addFilter("demodulize", (s) => inflection.demodulize(String(s)));
  env.addFilter("tableize", (s) => inflection.tableize(String(s)));
  env.addFilter("classify", (s) => inflection.classify(String(s)));
  env.addFilter("foreign_key", (s, dropIdUBar = false) =>
    inflection.foreign_key(String(s), !!dropIdUBar),
  );
  env.addFilter("ordinalize", (s) => inflection.ordinalize(String(s)));
  env.addFilter("transform", (s, arr = []) =>
    inflection.transform(String(s), Array.isArray(arr) ? arr : [arr]),
  );

  return env;
}

/**
 * Get or create cached environment instance
 */
function getEnv(binding) {
  const key = envKey(binding);
  let env = _envCache.get(key);
  if (!env) {
    env = createEnv(binding);
    _envCache.set(key, env);
  }
  return env;
}

/**
 * Ensure directory exists for file output
 */
async function ensureDir(filePath) {
  await fs.mkdir(dirname(filePath), { recursive: true });
}

/**
 * GitVan Template Engine
 *
 * Provides template rendering with inflection filters, config discovery,
 * and deterministic environment for reproducible builds.
 *
 * @param {Object} opts - Template options
 * @param {string[]} opts.paths - Custom template paths (overrides config discovery)
 * @param {boolean} opts.autoescape - Enable HTML auto-escaping
 * @param {boolean} opts.noCache - Disable template caching
 * @returns {Object} Template engine instance
 */
export async function useTemplate(opts = {}) {
  const binding = await bindContext(opts);
  const env = getEnv(binding);

  /**
   * Prepare base data for template rendering
   * Includes context data and deterministic timestamps
   */
  function baseData(extra) {
    const common = {};
    if (binding.nowISO) common.nowISO = binding.nowISO;
    if (binding.ctx) common.git = binding.ctx;
    return { ...common, ...(extra || {}) };
  }

  return {
    /**
     * Render template from file
     * @param {string} templateName - Template file name
     * @param {Object} data - Template data
     * @returns {string} Rendered content
     */
    render(templateName, data = {}) {
      return env.render(String(templateName), baseData(data));
    },

    /**
     * Render template from string
     * @param {string} templateStr - Template string
     * @param {Object} data - Template data
     * @returns {string} Rendered content
     */
    renderString(templateStr, data = {}) {
      return env.renderString(String(templateStr), baseData(data));
    },

    /**
     * Render template to file
     * @param {string} templateName - Template file name
     * @param {string} outPath - Output file path
     * @param {Object} data - Template data
     * @returns {Object} { path: string, bytes: number }
     */
    async renderToFile(templateName, outPath, data = {}) {
      const abs = isAbsolute(outPath) ? outPath : join(binding.root, outPath);
      await ensureDir(abs);
      const text = env.render(String(templateName), baseData(data));
      const buf = Buffer.from(text, "utf8");
      await fs.writeFile(abs, buf);
      return { path: outPath, bytes: buf.length };
    },

    /**
     * Get the underlying Nunjucks environment
     * @returns {nunjucks.Environment}
     */
    get env() {
      return env;
    },

    /**
     * Get template paths being used
     * @returns {string[]}
     */
    get paths() {
      return binding.paths;
    },

    /**
     * Get root directory
     * @returns {string}
     */
    get root() {
      return binding.root;
    },
  };
}

/**
 * Synchronous version for backward compatibility
 * Note: This version cannot use config discovery and relies on context only
 */
export function useTemplateSync(opts = {}) {
  let ctx;
  try {
    ctx = useGitVan();
  } catch {
    ctx = tryUseGitVan?.() || null;
  }

  const root = (ctx && ctx.cwd) || process.cwd();
  const cfgDirs =
    (ctx && ctx.config && ctx.config.templates && ctx.config.templates.dirs) ||
    [];
  const paths = (
    Array.isArray(opts.paths) && opts.paths.length
      ? opts.paths
      : cfgDirs.length
        ? cfgDirs
        : ["templates"]
  ).map((p) => presolve(root, p));

  const autoescape =
    typeof opts.autoescape === "boolean"
      ? opts.autoescape
      : ((ctx && ctx.config?.templates?.autoescape) ?? false);

  const noCache =
    typeof opts.noCache === "boolean"
      ? opts.noCache
      : ((ctx && ctx.config?.templates?.noCache) ?? true);

  const nowISO =
    typeof ctx?.now === "function" ? ctx.now() : process.env.GITVAN_NOW || null;

  const binding = { root, paths, autoescape, noCache, nowISO, ctx };
  const env = getEnv(binding);

  function baseData(extra) {
    const common = {};
    if (binding.nowISO) common.nowISO = binding.nowISO;
    if (binding.ctx) common.git = binding.ctx;
    return { ...common, ...(extra || {}) };
  }

  return {
    render(templateName, data = {}) {
      return env.render(String(templateName), baseData(data));
    },
    renderString(templateStr, data = {}) {
      return env.renderString(String(templateStr), baseData(data));
    },
    async renderToFile(templateName, outPath, data = {}) {
      const abs = isAbsolute(outPath) ? outPath : join(binding.root, outPath);
      await ensureDir(abs);
      const text = env.render(String(templateName), baseData(data));
      const buf = Buffer.from(text, "utf8");
      await fs.writeFile(abs, buf);
      return { path: outPath, bytes: buf.length };
    },
    get env() {
      return env;
    },
    get paths() {
      return binding.paths;
    },
    get root() {
      return binding.root;
    },
  };
}
